.flex-container {
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
}

.full-height {
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
}

body {
  background: linear-gradient(to right, red, yellow);
}

.vertical-center {
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
}

.overlay {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.5);
}

button {
  background: none;
  border: none;
  padding: 0;
  font: inherit;
}

.modal {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 999;
}

.grid {
  display: flex;
  flex-wrap: wrap;
}
.grid-item {
  flex: 1 1 calc(33.333% - 10px);
  margin: 5px;
}

.text-ellipsis {
  overflow: hidden;
  white-space: nowrap;
  text-overflow: ellipsis;
}

input[type='checkbox'] {
  display: none;
}
.custom-checkbox {
  position: relative;
  padding-left: 25px;
}
.custom-checkbox:before {
  content: '';
  position: absolute;
  left: 0;
  top: 0;
  width: 20px;
  height: 20px;
  border: 1px solid #000;
}

@keyframes fadeIn {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}
.element {
  animation: fadeIn 1s;
}

@keyframes colorChange {
  0% {
    color: red;
  }
  100% {
    color: blue;
  }
}
.text {
  animation: colorChange 3s infinite;
}

button {
  padding: 10px 20px;
  border: none;
  border-radius: 5px;
  background-color: #3498db;
  color: white;
  cursor: pointer;
}

.floating-label {
  position: relative;
}
.floating-label input:focus + label,
.floating-label input:not(:placeholder-shown) + label {
  top: -20px;
  font-size: 12px;
}

.grid-item {
  display: flex;
  justify-content: center;
  align-items: center;
}

.flip-card:hover .flip-card-inner {
  transform: rotateY(180deg);
}
.flip-card-front, .flip-card-back {
  position: absolute;
  width: 100%;
  backface-visibility: hidden;
}
.flip-card-back {
  transform: rotateY(180deg);
}

.tooltip {
  position: relative;
  display: inline-block;
}
.tooltip .tooltip-text {
  visibility: hidden;
  width: 120px;
  background-color: black;
  color: #fff;
  text-align: center;
  border-radius: 5px;
  padding: 5px;
  position: absolute;
  z-index: 1;
  bottom: 125%;
  left: 50%;
  margin-left: -60px;
}
.tooltip:hover .tooltip-text {
  visibility: visible;
}

@keyframes pulse {
  0% {
    transform: scale(1);
  }
  50% {
    transform: scale(1.1);
  }
  100% {
    transform: scale(1);
  }
}
.pulse {
  animation: pulse 2s infinite;
}

.overlay-text {
  position: relative;
}
.overlay-text::after {
  content: 'Overlay Text';
  position: absolute;
  top: 0;
  left: 0;
  color: white;
  background-color: rgba(0, 0, 0, 0.5);
  padding: 10px;
}

.parallax {
  background-image: url('image.jpg');
  height: 500px;
  background-attachment: fixed;
  background-position: center;
  background-repeat: no-repeat;
  background-size: cover;
}

.gradient-text {
  background: linear-gradient(to right, red, yellow);
  -webkit-background-clip: text;
  color: transparent;
}
