.animate {
  animation: example 2s infinite;
  /* Animation properties: name, duration, and repeat count */
}

@keyframes example {
  from { opacity: 0; }
  to { opacity: 1; }
  /* Animates from 0% to 100% (start to end) */
}

.animate {
  animation-duration: 3s;
  /* Duration in seconds or milliseconds */
}

.animate {
  animation-timing-function: ease-in-out;
  /* Options: linear | ease | ease-in | ease-out | ease-in-out | cubic-bezier */
}

.animate {
  animation-delay: 1s;
  /* Delay before the animation starts */
}

.animate {
  animation-iteration-count: infinite;
  /* Options: a number or 'infinite' for continuous repetition */
}

.animate {
  animation-direction: alternate;
  /* Options: normal | reverse | alternate | alternate-reverse */
}

.animate {
  animation-fill-mode: forwards;
  /* Options: none | forwards | backwards | both */
}

.animate {
  animation: fade 2s ease-in, slide 1s ease-out;
  /* Multiple animations separated by commas */
}

.animate {
  animation-play-state: paused;
  /* Options: running | paused */
}

.animate {
  animation: example 4s 2s infinite alternate both;
  /* shorthand: name, duration, delay, iteration-count, direction, fill-mode */
}

.box {
  transition: all 0.5s ease;
  /* Animates all properties over 0.5 seconds with easing */
}

.box {
  transition: background-color 0.5s ease, transform 0.3s ease-out;
  /* Animates background-color and transform separately */
}

@keyframes move {
  0% { transform: translateX(0); }
  100% { transform: translateX(100px); }
  /* Moves element horizontally from 0 to 100px */
}

@keyframes colorChange {
  0% { background-color: red; }
  100% { background-color: blue; }
  /* Changes background color from red to blue */
}

@keyframes bounce {
  0%, 20%, 50%, 80%, 100% { transform: translateY(0); }
  40% { transform: translateY(-30px); }
  60% { transform: translateY(-15px); }
}

@keyframes spin {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
  /* Rotates element 360 degrees */
}

@keyframes textColor {
  0% { color: red; }
  50% { color: yellow; }
  100% { color: green; }
  /* Changes text color through keyframes */
}

@keyframes scale {
  0% { transform: scale(1); }
  50% { transform: scale(1.5); }
  100% { transform: scale(1); }
  /* Enlarges and then returns to original size */
}

@keyframes fade {
  0% { opacity: 1; }
  50% { opacity: 0.5; }
  100% { opacity: 1; }
  /* Adjusts opacity through keyframes */
}

@keyframes slide {
  0% { transform: translateX(-100%); }
  100% { transform: translateX(0); }
  /* Slides element in from the left */
}

@keyframes pulse {
  0% { transform: scale(1); }
  50% { transform: scale(1.1); }
  100% { transform: scale(1); }
  /* Increases and decreases size like a pulse */
}

@keyframes wobble {
  0% { transform: translateX(0); }
  15% { transform: translateX(-25px) rotate(-5deg); }
  30% { transform: translateX(20px) rotate(3deg); }
 45% { transform: translateX(-15px) rotate(-3deg); }
 60% { transform: translateX(10px) rotate(2deg); }
 75% { transform: translateX(-5px) rotate(-1deg); }
 100% { transform: translateX(0); }
}

@keyframes shake {
  0%, 100% { transform: translateX(0); }
  10%, 30%, 50%, 70%, 90% { transform: translateX(-10px); }
  20%, 40%, 60%, 80% { transform: translateX(10px); }
  /* Shakes element horizontally */
}
